import React, { useState } from "react";
import { Link } from "react-router-dom";
import Navbar from "./Navbar";
import PaletteFooter from "./PaletteFooter";
import ColorBox from "./ColorBox";
import { withStyles } from "@material-ui/styles";

const styles = {
  Palette: { height: "100vh", display: "flex", flexDirection: "column" },
  colors: { height: "90%", display: "flex", flexWrap: "wrap" },
  goBack: {
    width: "20%",
    height: "50%",
    position: "relative",
    cursor: "pointer",
    opacity: 1,
    backgroundColor: "black",
    "& a": {
      color: "white",
      width: "100px",
      height: "30px",
      position: "absolute",
      display: "inline-block",
      top: "50%",
      left: "50%",
      marginLeft: "-50px",
      marginTop: "-15px",
      textAlign: "center",
      outline: "none",
      background: "rgba(255, 255, 255, 0.3)",
      fontSize: "1rem",
      lineHeight: "30px",
      textTransform: "uppercase",
      cursor: "pointer"
    }
  }
};

function SingleColorPalette(props) {
  const _shades = gatherShades(props.palette, props.colorId);
  const [format, setFormat] = useState("hex");
  const { paletteName, emoji, id } = props.palette;
  const { classes } = props;
  const changeFormat = val => {
    setFormat(val);
  };
  const colorBoxes = _shades.map(color => (
    <ColorBox
      key={color.name}
      name={color.name}
      background={color[format]}
      showingFullPalette={false}
    />
  ));
  return (
    <div className={classes.Palette}>
      <Navbar handleChange={changeFormat} showingAllColors={false} />
      <div className={classes.colors}>
        {colorBoxes}
        <div className={classes.goBack}>
          <Link to={`/palette/${id}`}>GO BACK</Link>
        </div>
      </div>
      <PaletteFooter paletteName={paletteName} emoji={emoji} />
    </div>
  );
}

const gatherShades = (palette, colorToFilterBy) => {
  let shades = [];
  let allColors = palette.colors;
  for (let key in allColors) {
    shades = shades.concat(
      allColors[key].filter(color => color.id === colorToFilterBy)
    );
  }
  //return all shades of given colors
  return shades.slice(1);
};

export default withStyles(styles)(SingleColorPalette);
